import gql from 'graphql-tag';
import { Query, Mutation } from 'react-apollo';
import { Card,
    ResourceList,
    Stack,
    TextStyle,
    TextField,
    Thumbnail,
    DataTable,
    ButtonGroup,
    Button
 } from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
import { useMutation } from '@apollo/react-hooks';
const UPDATE_PRODUCTS = gql`
  mutation productUpdate($input: ProductInput!) {
    productUpdate(input: $input) {
      product {
        id
        title
        handle
        description
      }
      userErrors {
        field
        message
      }
    }
  }
`;
const SEARCH_PRODUCTS = gql`
  query getProducts($searchquery: String!) {
    products(query: $searchquery first: 50) {
      edges {
        node {
          title
          handle
          description
          id
          images(first: 1) {
            edges {
              node {
                originalSrc
                altText
              }
            }
          }
          variants(first: 1) {
            edges {
              node {
                price
                id
              }
            }
          }
        }
      }
    }
  }
`;
function ReplaceButton(props){
  console.log(props)
  const [updateProduct, {loading, error}] = useMutation(UPDATE_PRODUCTS);
  const handleReplace = () => () => {
    if(!props.products || props.products.length<1){
      return 
    }
    props.products.edges.map(item=>{
      updateProduct({variables:{input:transformData(item.node)}})
    })
  }
  const transformData = (data)=>{
    const searchquery = new RegExp(props.searchquery, "gi");
    const result = {
      id:data.id,
      title: data.title.replace(searchquery, props.replacestring),
      handle: data.handle.replace(searchquery, props.replacestring),
      descriptionHtml: `<p>${data.description.replace(searchquery, props.replacestring)}</p>`,
    }
    return result
  }
  return (      
  <ButtonGroup>
    <Button primary onClick={handleReplace()}>Replace Once</Button>
    <Button primary onClick={handleReplace()}>Replace All</Button>
  </ButtonGroup>
  )
}
class ResourceListWithProducts extends React.Component {
    static contextType = Context;
    state = {
        searchquery: "",
        replacestring:""
    }

    handleChange = (field) => {
      return (value) => this.setState({ [field]: value });
    };

    handleReplace = () => {
      if(!this.state.products || this.state.products.length<1){
        return 
      }
      this.state.products.edges.map(item=>{
        this.props.apolloClient.mutate({
          mutation: UPDATE_PRODUCTS,
          variables:{input:this.transformData(item.node)}
        })
        .then(response=>{
          console.log(response)
        })
      })
    }
    transformData = (data)=>{
      const searchquery = new RegExp(this.state.searchquery, "gi");
      const result = {
        id:data.id,
        title: data.title.replace(searchquery, this.state.replacestring),
        handle: data.handle.replace(searchquery, this.state.replacestring),
        descriptionHtml: `<p>${data.description.replace(searchquery, this.state.replacestring)}</p>`,
      }
      return result
    }

    InjectHighlight = (text) => {
      const replace = new RegExp(this.state.searchquery, "gi");
      return (
        <span
          dangerouslySetInnerHTML={{
            __html : text.replace(replace, function (x) {
              return `<span style="background-color:yellow">${x}</span>`;
            })
          }} />
      )
    }

    ConvertDatatoTable = (data) =>{
      if (!data || data.length < 1){
        return [[]]
      }
      return data.edges.map(item=>{
        const node = item.node
        return [this.InjectHighlight(node.title), this.InjectHighlight(node.handle), this.InjectHighlight(node.description)]
      })
    }

    requery = () =>{
      this.setState({searchquery:"",replacestring:""})
    }

    render() {
      const app = this.context;
      const redirectToProduct = () => {
        const redirect = Redirect.create(app);
        redirect.dispatch(
          Redirect.Action.APP,
          '/edit-products',
        );
      };
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();
    return (
      <div>
      <TextField label="search" value={this.state.searchquery} onChange={this.handleChange('searchquery')} />
      <TextField label="replace" value={this.state.replacestring} onChange={this.handleChange('replacestring')} />
      {/* <ReplaceButton products = {this.state.products} searchquery={this.state.searchquery} replacestring={this.state.replacestring} /> */}
      <ButtonGroup>
        <Button primary onClick={this.handleReplace.bind(this)}>Replace Once</Button>
        <Button primary onClick={this.handleReplace.bind(this)}>Replace All</Button>
      </ButtonGroup>
      <Query query={SEARCH_PRODUCTS} variables={{ searchquery: this.state.searchquery }} fetchPolicy="no-cache" onCompleted={(data)=>{
          this.setState({products : data.products})}}>
        {({ data, loading, error }) => {
          if (loading) return <div>Loading…</div>;
          if (error) return <div>{error.message}</div>;
          const rows = this.ConvertDatatoTable(data.products)
          console.log(rows)
          return (
            <Card>
              <DataTable
                columnContentTypes={[
                  'text',
                  'text',
                  'text',
                ]}
                headings={[
                  'title',
                  'handle',
                  'description',
                ]}
                rows={rows}
              />
            </Card>
          );
        }}
      </Query>

      </div>
    );
  }
}

 export default ResourceListWithProducts;
