import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { Card,
    ResourceList,
    Stack,
    TextStyle,
    TextField,
    Thumbnail,
    DataTable
 } from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';

const SEARCH_PRODUCTS = gql`
  query getProducts($searchquery: String!) {
    products(query: $searchquery first: 50) {
      edges {
        node {
          title
          handle
          description
          id
          images(first: 1) {
            edges {
              node {
                originalSrc
                altText
              }
            }
          }
          variants(first: 1) {
            edges {
              node {
                price
                id
              }
            }
          }
        }
      }
    }
  }
`;

class ResourceListWithProducts extends React.Component {
    static contextType = Context;
    state = {
        searchquery: "LED"
    }

    handleChange = (field) => {
      return (value) => this.setState({ [field]: value });
    };

    InjectHighlight = (text) => {
      const replace = new RegExp(this.state.searchquery, "gi");
      return (
        <span
          dangerouslySetInnerHTML={{
            __html : text.replace(replace, function (x) {
              return `<span style="background-color:yellow">${x}</span>`;
            })
          }} />
      )
    }
    ConvertDatatoTable = (data) =>{
      if (!data || data.length < 1){
        return [[]]
      }
      return data.edges.map(item=>{
        const node = item.node
        return [this.InjectHighlight(node.title), this.InjectHighlight(node.handle), this.InjectHighlight(node.description), node.id]
      })
    }

    render() {
      const app = this.context;
      const redirectToProduct = () => {
        const redirect = Redirect.create(app);
        redirect.dispatch(
          Redirect.Action.APP,
          '/edit-products',
        );
      };
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();
    return (
      <div>
      <TextField label="search" value={this.state.searchquery} onChange={this.handleChange('searchquery')} />
      <TextField label="replace" value={this.state.replace} onChange={this.handleChange('searchquery')} />
      <Query query={SEARCH_PRODUCTS} variables={{ searchquery: this.state.searchquery }}>
        {({ data, loading, error }) => {
          if (loading) return <div>Loadingâ€¦</div>;
          if (error) return <div>{error.message}</div>;
          console.log(data);
          const rows = this.ConvertDatatoTable(data.products)
          console.log(rows)
          return (
            <Card>
              <DataTable
                columnContentTypes={[
                  'text',
                  'text',
                  'text',
                  'text',
                ]}
                headings={[
                  'title',
                  'handle',
                  'description',
                  'id'
                ]}
                rows={rows}
              />
            </Card>
          );
        }}
      </Query>

      </div>
    );
  }
}

 export default ResourceListWithProducts;
