import gql from 'graphql-tag';
import { Query, Mutation } from 'react-apollo';
import { Card,
    ResourceList,
    Stack,
    TextStyle,
    TextField,
    Thumbnail,
    DataTable,
    Button,
    Checkbox,
    Icon
 } from '@shopify/polaris';
 import {
  StarFilledMinor,
  StarOutlineMinor
} from '@shopify/polaris-icons';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
import { useMutation } from '@apollo/react-hooks';
import _ from 'lodash'
import './ResourceList.css'
import{ UPDATE_PRODUCTS, SEARCH_PRODUCTS }from "./graphql.js"

class ResourceListWithProducts extends React.Component {
    static contextType = Context;
    state = {
        searchquery: "",
        replacestring:"",
        matchcase:false,
        saved: false
    }

    toggleFavorite = () => {
      console.log("toggle fav")
      const favorite = store.get('favorite')
      const searchform = _.pick(this.state, ['searchquery', 'replacestring', 'matchcase'])
      const hashedfav = Object.keys(searchform).sort().map(x => searchform[x].toString()).join(";");
      if(!this.state.saved){
        if (!store.get('favorite')){
          console.log("no current fav")
          console.log("hashedfav:" + hashedfav)
          store.set("favorite",{[hashedfav]:searchform})
        }else{
          console.log(store.get('favorite'))
          store.set("favorite",{...favorite, [hashedfav]:searchform})
        }
        this.setState({ saved:true })
      }else{
        delete favorite[hashedfav]
        store.set("favorite",favorite)
        this.setState({ saved:false })
      }
    }

    handleChange = (field) =>  (value) => {
      this.setState({ [field]: value }, ()=>{
        console.log(field)
        const searchform = _.pick(this.state, ['searchquery', 'replacestring', 'matchcase'])
        const hashedfav = Object.keys(searchform).sort().map(x => searchform[x].toString()).join(";");
        console.log(store.get('favorite'))
        console.log(hashedfav)
        console.log(store.get('favorite')[hashedfav])
        const saved = store.get('favorite') && store.get('favorite')[hashedfav]
        console.log(saved)
        this.setState({ saved })
      })
    };

    handleReplace = () => {
      if(!this.state.products || this.state.products.length<1){
        return 
      }
      this.state.products.edges.map(item=>{
        this.props.apolloClient.mutate({
          mutation: UPDATE_PRODUCTS,
          variables:{input:this.transformData(item.node)}
        })
        .then(response=>{
          console.log(response)
        })
      })
      this.setState({searchquery:"",replacestring:""})
    }

    transformData = (data)=>{
      const searchquery = new RegExp(this.state.searchquery, "gi");
      const result = {
        id:data.id,
        title: data.title.replace(searchquery, this.state.replacestring),
        handle: data.handle.replace(searchquery, this.state.replacestring),
        descriptionHtml: `<p>${data.description.replace(searchquery, this.state.replacestring)}</p>`,
      }
      return result
    }

    InjectHighlight = (text) => {
      const replace = new RegExp(this.state.searchquery, "gi");
      return (
        <span
          dangerouslySetInnerHTML={{
            __html : text.replace(replace, function (x) {
              return `<span style="background-color:yellow">${x}</span>`;
            })
          }} />
      )
    }

    ConvertDatatoTable = (data) =>{
      if (!data || data.length < 1){
        return [[]]
      }
      return data.edges.map(item=>{
        const node = item.node
        return [this.InjectHighlight(node.title), this.InjectHighlight(node.handle), this.InjectHighlight(node.description)]
      })
    }

    render() {
      const app = this.context;
      const redirectToProduct = () => {
        const redirect = Redirect.create(app);
        redirect.dispatch(
          Redirect.Action.APP,
          '/edit-products',
        );
      };
    return (
      <div>
        <div className="form-container">

          <div className="form-row">
            <div className="form-input" >
              <TextField placeholder="Find" value={this.state.searchquery} onChange={this.handleChange('searchquery')} />
            </div>
          </div>

          <div className="form-row">
            <div className="form-input" >
              <TextField placeholder="Replace with"  value={this.state.replacestring} onChange={this.handleChange('replacestring')} />
            </div>
            <Button className="form-button" onClick={this.handleReplace.bind(this)}>Replace </Button>
            <Button className="form-button" onClick={this.handleReplace.bind(this)}>Replace all</Button>
          </div>


          <div className="form-row">
            <Checkbox label="Match case" checked={this.state.matchcase} onChange={this.handleChange('matchcase')} />
            <a onClick={this.toggleFavorite.bind(this)} >
              <Icon source={this.state.saved ? StarFilledMinor : StarOutlineMinor} />
              {this.state.saved ? "Saved" : "Save to Favorite"}
            </a>
          </div>

        </div>
      {/* <Query query={SEARCH_PRODUCTS} variables={{ searchquery: this.state.searchquery }} 
    pollInterval={1500} onCompleted={(data)=>{
          this.setState({products : data.products})}}>
        {({ data, loading, error }) => {
          if (loading) return <div>Loading…</div>;
          if (error) return <div>{error.message}</div>;
          const rows = this.ConvertDatatoTable(data.products)
          return (
            <Card>
              <DataTable
                columnContentTypes={[
                  'text',
                  'text',
                  'text',
                ]}
                headings={[
                  'title',
                  'handle',
                  'description',
                ]}
                rows={rows}
              />
            </Card>
          );
        }}
      </Query> */}

      </div>
    );
  }
}

 export default ResourceListWithProducts;
