import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { Card,
    ResourceList,
    Stack,
    TextStyle,
    TextField,
    Thumbnail,
 } from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';

const SEARCH_PRODUCTS = gql`
  query getProducts($searchquery: String!) {
    products(query: $searchquery) {
      title
      handle
      descriptionHtml
      id
      images(first: 1) {
        edges {
          node {
            originalSrc
            altText
          }
        }
      }
      variants(first: 1) {
        edges {
          node {
            price
            id
          }
        }
      }
    }
  }
`;

class ResourceListWithProducts extends React.Component {
    static contextType = Context;
    state = {
        searchquery: ""
    }

    handleChange = (field) => {
      return (value) => this.setState({ [field]: value });
    };
    render() {
      const app = this.context;
      const redirectToProduct = () => {
        const redirect = Redirect.create(app);
        redirect.dispatch(
          Redirect.Action.APP,
          '/edit-products',
        );
      };
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();
    return (
      <div>
      <TextField label="search" value={this.state.searchquery} onChange={this.handleChange('searchquery')} />
      <Query query={SEARCH_PRODUCTS} variables={{ searchquery: this.state.searchquery }}>
        {({ data, loading, error }) => {
          if (loading) return <div>Loadingâ€¦</div>;
          if (error) return <div>{error.message}</div>;
          console.log(data);
          return (
            <Card>
              <ResourceList
                showHeader
                resourceName={{ singular: 'Product', plural: 'Products' }}
                items={data.nodes}
                renderItem={item => {
                  const media = (
                    <Thumbnail
                      source={
                        item.images.edges[0]
                          ? item.images.edges[0].node.originalSrc
                          : ''
                      }
                      alt={
                        item.images.edges[0]
                          ? item.images.edges[0].node.altText
                          : ''
                      }
                    />
                  );
                  const price = item.variants.edges[0].node.price;
                  return (
                    <ResourceList.Item
                      id={item.id}
                      media={media}
                      accessibilityLabel={`View details for ${item.title}`} 
                      onClick={() => {
                        store.set('item', item);
                        redirectToProduct();
                      }}
                    >
                      <Stack>
                        <Stack.Item fill>
                          <h3>
                            <TextStyle variation="strong">
                              {item.title}
                            </TextStyle>
                          </h3>
                        </Stack.Item>
                        <Stack.Item>
                          <p>${price}</p>
                        </Stack.Item>
                        <Stack.Item>
                          <p>Expires on {twoWeeksFromNow} </p>
                        </Stack.Item>
                      </Stack>
                    </ResourceList.Item>
                  );
                }}
              />
            </Card>
          );
        }}
      </Query>

      </div>
    );
  }
}

 export default ResourceListWithProducts;
